import { Button, Card, CardContent, CardMedia } from "@mui/material";
import { InfoItem } from "../atoms/InfoItem";


interface Props {
  game_image: string;
  publisher: string;
  developer: string;
  release_date: string;
  platform: string;
}

export const GameInfoBasics: React.FC<Props> = (game) => {
  return (
    <Card
      sx={{
        maxWidth: "20rem",
        marginTop: "5rem",
        backgroundColor: "rgba(0,0,0,0)",
        boxShadow: "0",
      }}
    >
      <CardMedia
        sx={{
          aspectRatio: "16/9",
          maxWidth: "100%",
          borderRadius: 1,
        }}
        component="img"
        alt={game.developer}
        image={game.game_image}
      />
      {/* <Button variant="outlined" sx={{color: "white", fontWeight: "400"}} fullWidth>Add to Favorites</Button> */}
      <CardContent sx={{padding: "0 8px"}}>
          {Object.entries(game).map(([key, value]) => value !== game.game_image && <InfoItem key={value} first={key} second={value} />)}
      </CardContent>
    </Card>
  );
};
