import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styles from "../UI/modules/Header.module.css";
import { IoGameController } from "react-icons/io5";
import { Input } from "@mui/material";

export const Header: React.FC = () => {
  const [pressed, setPressed] = useState("/");

  const activeStyle = {
    color: "#fff",
  };

  return (
    <header className={styles.header}>
      <div className={`${styles.container} container`}>
        <div className={styles.leftpart}>
          <NavLink
            to="/"
            onClick={() => {
              setPressed("/");
            }}
          >
            <IoGameController className={styles.logo} />
          </NavLink>
          {/* <Input value="aa" /> */}
        </div>
        <nav className={styles.navigation}>
          <ul>
            <li>
              <NavLink
                to="/"
                className={pressed === "/" ? styles.activeStyle : ""}
                onClick={() => {
                  setPressed("/");
                }}
              >
                Catalog
              </NavLink>
            </li>
            <li>
              <NavLink
                to="popular"
                className={pressed === "/popular" ? styles.activeStyle : ""}
                onClick={() => {
                  setPressed("/popular");
                }}
              >
                Popular
              </NavLink>
            </li>
            <li>
              <NavLink
                to="favorites"
                className={pressed === "/favorites" ? styles.activeStyle : ""}
                onClick={() => {
                  setPressed("/favorites");
                }}
              >
                Favorites
              </NavLink>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};
