import { Slider } from "../atoms/Slider";
import { GameDetails } from "../../../model/game.model";
import { GameInfoBasics } from "../molecules/GameInfoBasics";
import styles from "../modules/GameInfo.module.css";
import { Typography } from "@mui/material";

interface Props {
  game: GameDetails;
}

export const GameInfo: React.FC<Props> = ({ game }) => {
  const checked = game.description.split(". ");
  return (
    <div className={styles.container}>
      <div>
        <Slider
          images={game.screenshots.map((item) => item.image)}
          title={game.title}
        />

        <Typography
          sx={{
            marginTop: "2rem",
            fontWeight: "400",
            maxWidth: "50vw",
            color: "#F5F5F599",
          }}
          variant="body2"
        >
          {game.description.substring(0,game.description.indexOf(checked.length > 3 ? checked[2] : checked [1]))}
        </Typography>
        <Typography
          sx={{
            marginTop: "2rem",
            fontWeight: "400",
            maxWidth: "50vw",
            color: "F5F5F599",
            lineHeight: "25px"
          }}
          variant="body2"
        >
          {game.description.substring(game.description.indexOf(checked.length > 3 ? checked[2] : checked [1]))}
        </Typography>
      </div>
      <GameInfoBasics
        game_image={game.thumbnail}
        publisher={game.publisher}
        developer={game.developer}
        release_date={game.release_date}
        platform={game.platform}
      />
    </div>
  );
};
