import { Slider } from "../atoms/Slider";
import { GameDetails } from "../../../model/game.model";
import { GameInfoBasics } from "../molecules/GameInfoBasics";
import styles from "../modules/GameInfo.module.css";
import { Typography } from "@mui/material";

interface Props {
  game: GameDetails;
}

export const GameInfo: React.FC<Props> = ({ game }) => {
  return (
    <div className={styles.container}>
      <div>
        <Slider
          images={game.screenshots.map((item) => item.image)}
          title={game.title}
        />
        <Typography sx={{marginTop: "2rem", fontWeight: "400"}} variant="body1"> {game.short_description}</Typography>
      </div>
      <GameInfoBasics
        game_image={game.thumbnail}
        publisher={game.publisher}
        developer={game.developer}
        release_date={game.release_date}
        platform={game.platform}
      />
    </div>
  );
};
