interface Props {
  first: string;
  second: string;
}

export const ReqsItem: React.FC<Props> = (reqs) => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
        width: "100%",
        padding: "0 8px",
      }}
    >
      <span style={{ color: "white" }}>
        {reqs.first[0].toUpperCase() + reqs.first.substring(1)}
      </span>
      <span style={{ color: "white" }}>
        {reqs.second.indexOf("or") && reqs.second.split("or")[0]}
      </span>
      {/* <span>{reqs.second}</span> */}
    </div>
  );
};
