import { Button, Card, CardMedia } from "@mui/material";
import { fontWeight } from "@mui/system";


interface Props {
  game_image: string;
  publisher: string;
  developer: string;
  release_date: string;
  platform: string;
}

export const GameInfoBasics: React.FC<Props> = (game) => {
  return (
    <Card
      sx={{
        maxWidth: "20rem",
        marginTop: "5rem",
        backgroundColor: "rgba(0,0,0,0)",
        boxShadow: "0",
      }}
    >
      <CardMedia
        sx={{ aspectRatio: "16/9", maxWidth: "100%", borderRadius: 1, marginBottom: "2rem"}}
        component="img"
        alt={game.developer}
        image={game.game_image}
      />
      <Button variant="outlined" sx={{color: "white", fontWeight: "400"}} fullWidth>Add to Favorites</Button>
    </Card>
  );
};
