import { Card, CardMedia } from "@mui/material";

interface Props {
  game_image: string;
  publisher: string;
  developer: string;
  release_date: string;
  platform: string;
}

export const GameInfoBasics: React.FC<Props> = (game) => {
  return (
    <Card
      className={styles.container}
      sx={{
        maxWidth: "18rem",
        margin: "0.5rem",
        backgroundColor: "rgba(0,0,0,0)",
        boxShadow: "0",
      }}
    >
      <CardMedia
        sx={{ aspectRatio: "16/9", maxWidth: "100%", borderRadius: 1 }}
        component="img"
        alt={game.developer}
        image={game.game_image}
      />{" "}
    </Card>
  );
};
