import { Button, Card, CardContent, CardMedia } from "@mui/material";
import { InfoItem } from "../atoms/InfoItem";

interface Props {
  game_image: string;
  publisher: string;
  developer: string;
  release_date: string;
  platform: string;
}

export const GameInfoBasics: React.FC<Props> = (game) => {
  return (
    <Card
      sx={{
        marginLeft: "2rem",
        maxWidth: "20rem",
        marginTop: "5rem",
        backgroundColor: "rgba(0,0,0,0)",
        boxShadow: "0",
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
      }}
    >
      <CardMedia
        sx={{
          aspectRatio: "16/9",
          maxWidth: "20rem",
          borderRadius: 1,
        }}
        component="img"
        alt={game.developer}
        image={game.game_image}
      />

      <CardContent sx={{ padding: "0 8px" }}>
        {Object.entries(game).map(
          ([key, value]) =>
            value !== game.game_image && (
              <InfoItem key={value} first={key} second={value} />
            )
        )}
      </CardContent>
      <Button variant="outlined" sx={{color: "white", fontWeight: "400", marginTop: "1rem", maxWidth: "80%"}} fullWidth>Add to Favorites</Button>
    </Card>
  );
};
