import { Divider, Typography } from "@mui/material";

interface Props {
  key: string;
  value: string;
}

export const InfoItem: React.FC<Props> = (info) => {
    const keyChanger = () => {
        if (info.key == "release_date") return "Release Date"
        else return info.key[0].toUpperCase() + info.key.substring(1)
    }
  return (
    <>
      <div style={{display: "flex", justifyContent: "space-between", marginTop: "1rem", color: "white"}}>
        <Typography variant="body2">{info.key}</Typography>
        <Typography variant="body2">{info.value}</Typography>
      </div>
      <Divider light />
    </>
  );
};
