import { Typography } from "@mui/material";
import SimpleImageSlider from "react-simple-image-slider";

interface Props {
  images: Array<string>;
  title: string
}

export const Slider: React.FC<Props> = ({ images, title }) => {
  return (
    <>
      <Typography variant="h4" sx={{margin: "0.5rem"}}>{title}</Typography>
      <SimpleImageSlider
        autoPlay
        autoPlayDelay={5}
        slideDuration={0.5}
        width="50vw"
        height="calc(9/16*50vw)"
        style={{aspectRatio: "16/9"}}
        images={images}
        showBullets={true}
        showNavs={true}
      />
    </>
  );
};
