import { Game } from "../../../model/games.model";
import React from "react";
import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Chip,
  Typography,
} from "@mui/material";

interface PageProps {
  game: Game;
}

export const GamesListItem: React.FC<PageProps> = ({ game }) => {
  return (
    <Card sx={{ maxWidth: "18rem", margin: "0.5rem", backgroundColor: "rgba(0,0,0,0)", boxShadow: "0" }}>
      <CardMedia
        sx={{ aspectRatio: "16/9", maxWidth: "100%", borderRadius: 4}}
        component="img"
        alt={game.title}
        image={game.thumbnail}

      />
      <div
        style={{
          height: "30%",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-evenly",
          padding: "16px 16px 5px 16px",
        }}
      >
        <Typography gutterBottom variant="h6" component="div" color="white">
          {game.title}
        </Typography>
        <Typography variant="body2" color="white">
          {game.short_description.length > 80
            ? game.short_description.substring(0, 80) + "..."
            : game.short_description}
        </Typography>
      </div>
      <Chip label={game.genre} />
    </Card>
  );
};
